

package Arrays;

public class arrays3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a[][] = new int[8][8];
		int i,j,count=0;
		for(i=1;i<=7;i++) {
			for(j=1;j<=7;j++) {
				count++;
				
				System.out.print("\t"+count);
				
			}
			System.out.println(" ");
			System.out.println("");
		}
	}
}
